use travel_ticket_booking_web;

SELECT * FROM travel_ticket_booking_web.trips;
ALTER TABLE trips AUTO_INCREMENT = 1;


update travel_ticket_booking_web.trips set
img_url = 'https://www.countryholidaystravel.com/assets/new/images/about-img.png'
where id = 13;
select * from travel_ticket_booking_web.trips where endDate > curdate() and status = 1 order by price;

select * from travel_ticket_booking_web.trips where startDate >= "2023-12-15"
and 25000 <= price and price <= 50000 order by price;

select curdate();

delete from trips where id > 0;


create table trips (
	id int auto_increment primary key,
    tripName varchar(30),
    price double,
    img_url varchar(100),
    desc_url varchar(100),
    address varchar(50),
    startDate date,
    endDate date,
    status boolean
);


